package javasessions;

public class MathematicalOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
System.out.println(4/2);
System.out.println(4*2);
System.out.println(4-2);
System.out.println(22/2);
System.out.println(9.0/2);
System.out.println(9/2.0);
System.out.println(9F/2);

System.out.println(0/2);//0
//System.out.println(2/0);//java.lang.ArithmeticException: / by zero

System.out.println(2/0.0);//Infinity
//System.out.println(0/0);//java.lang.ArithmeticException: / by zero
System.out.println(2.0/0);//Infinity
System.out.println(2.0/0.0);

System.out.println(0.0/0.0);//NaN(Not a number)
//System.out.println(0/0);// java.lang.ArithmeticException
System.out.println(0.0/2.0);
System.out.println((float)9/0);//Infinity

System.out.println(0.0/0);//NaN
System.out.println(0/0.0);//NaN
System.out.println(0.0/0.0);//NaN
System.out.println(9/0.0);//Infinity



System.out.println('a'/2);
System.out.println(9%3);
System.out.println(10%3);
System.out.println(100%5);








	
	
	}
	

}
